<?php defined('SYSPATH') OR die('No direct access allowed.');class Client_code_version_Controller extends CController {		public function index() {		$app = CApp::instance();		$app->title(clang::__('Client Code Version'));				$data = cdata::get('current_client_version');		$code_version = "";		if($data&&isset($data["code_version"])) {			$code_version = $data["code_version"];		}		$code_version_array = explode(".",$code_version);		$major_version = "";		$minor_version = "";		$revision = "";				if(count($code_version_array)>0) $major_version = $code_version_array[0];		if(count($code_version_array)>1) $minor_version = $code_version_array[1];		if(count($code_version_array)>2) $revision = $code_version_array[2];								$post = $this->input->post();		if ($post!=null) {			$error = 0;			$error_message = "";					$major_version = $post["major_version"];			$minor_version = $post["minor_version"];			$revision = $post["revision"];			$code_version = $major_version;			if(strlen($minor_version)>0) $code_version.=".".$minor_version;			if(strlen($minor_version)>0&&strlen($revision)>0) $code_version.=".".$revision;						if($error==0) {				$data["code_version"] = $code_version;				cdata::set('current_client_version',$data);			}						if ($error==0) {								cmsg::add("success","Your Client Code Version Successfully Modified !");											} else {				cmsg::add("error",$error_message);							}					}										$f = $app->add_form()->set_enctype('multipart/form-data');		$w = $f->add_widget();		$w->set_title('Client Code Version');		$w->set_icon('cog');		$tzlist = ctimezone::timezone_list();		$div = $w->add_div()->add_class('row-fluid');				$w->add_field()->set_label("Major Version")->add_control('major_version','text')->add_validation('required')->set_value($major_version);		$w->add_field()->set_label("Minor Version")->add_control('minor_version','text')->add_validation('required')->set_value($minor_version);		$w->add_field()->set_label("Revision")->add_control('revision','text')->add_validation('required')->set_value($revision);				$acts = $w->add_action_list();		$act_next = $acts->add_action();				$act_next->set_label('Submit')->set_submit(true);								echo $app->render();			}	}